    mongoDBSRV = process.env.MONGO_ATLAS_URL_TEST || 'mongodb+srv://gonDev:1234@planeta-it.o7o4bsp.mongodb.net/TEST-planetaIT?retryWrites=true&w=majority'
}


export default {
    MONGO_ATLAS_URL: mongoDBSRV,
    PUERTO: process.env.PUERTO || 3002
}

